import {useCallback, useRef, useState} from 'react';

export default function UsingCallback(){

    const [a, setA] = useState(0)
    const [b, setB] = useState(0)
    const [update, setUpdate] = useState(true)


    const ChildFunc = useCallback(() => {

        ChildAdd()
        console.log("Here: " + update)

    }, [update]);


    return(
        <div>
            {ChildFunc()}

            <button onClick={()=>{
                setA((value)=>{
                    console.log("A: ")
                    console.log(value + 1)
                    return value + 1
                })
            }}> INC A</button>

            <button onClick={()=>{
                setB((value)=>{
                    console.log("B: ")
                    console.log( value + 1)
                    return value + 1
                })
            }}> INC B</button>

            <button onClick={()=>{
                setUpdate(!update)

            }}> Update </button>


        </div>

    );
}


function ChildAdd(props){

    let counterRef = useRef(1)
    counterRef.current += 1
    console.log(counterRef.current)


    return(
        <h1>RENDERED CHILD {counterRef.current}</h1>
    );

}
