import { useRef, useState, useMemo } from "react"

export default function UsingMemo (props){

    const [a, setA] = useState(0)
    const [b, setB] = useState(0)

    function BigCalcUseMemo(a,b){
        let t1 = performance.now()
        const addition = useMemo(() => calc(a,b), [a,b]);
        let t2 = performance.now()

        return(
            <h2>
                using useMemo:
                a + b = {t2-t1} ms
            </h2>
        )

    }

    function BigCalc(a,b){
        let t1 = performance.now()
        const ans = calc(a,b);
        let t2 = performance.now()

        return(
          <h2>
              without useMemo:
              a + b = {t2-t1} ms
          </h2>
        );
    }

    function calc(a, b){

        let loopA = 0
        let loopB = 0

        for(let i = 0; i < a; i++){
            loopA += i
        }
        for(let i = 0; i < b; i++){
            loopB += i
        }
        return(loopA+loopB);
    }

    function handleClickCalc(){
        BigCalcUseMemo(a,b);
        BigCalc(a,b);
    }

    function handleChangeA(event){
        setA(event.target.value)
    }
    function handleChangeB(event){
        setB(event.target.value)
    }

    return(
        <form>

            <label>Number A:</label>
            <input value={a} name="Email" onChange={handleChangeA}/>

            <label>Number B:</label>
            <input value={b} name="Password" onChange={handleChangeB}/>

            <button onClick={handleClickCalc}> Do the Calc!</button>
        </form>
    );

}
