import { useRef, useState, useMemo } from "react"

export default function NotUsingMemo (props){

    const [a, setA] = useState(0)
    const [b, setB] = useState(0)


    let t1 = performance.now();
    const BigCalcUseMemo = useMemo(() => calc(a,b), [a,b]);
    let t2 = performance.now();


    function memoHandle(){
        console.log("USING MEMO: ")
        console.log(t2-t1 + " Ms")
    }

    function handleSubmit(event){
        event.preventDefault()
    }


    return(
        <form onSubmit={handleSubmit}>

            <label>Number A:</label>
            <input value={a} name="Email" onChange={(event)=>{
                setA(event.target.value)
            }}/>

            <label>Number B:</label>
            <input value={b} name="Password" onChange={(event)=>{
                setB(event.target.value)
            }}/>

            <button onClick={memoHandle}> Do the Calc!</button>

        </form>
    );

}

function calc(a, b){

    let loopA = 0
    let loopB = 0

    for(let i = 0; i < a; i++){
        loopA += i
    }
    for(let i = 0; i < b; i++){
        loopB += i
    }
    return(loopA+loopB);
}
