import { useRef, useState, useMemo } from "react"


export default function Memo(){

    return(
        <div>
            <UsingMemo/>
            <NotUsingMemo/>
        </div>

    );

}

function UsingMemo (props){

    const [a, setA] = useState(0)
    const [b, setB] = useState(0)

    const Bigmul = useMemo(()=>{calc(a,b)},[a,b]);

    return(
        <form>

            <label>Number A:</label>
            <input value={a} name="Email" onChange={(event)=>{
                setA(event.target.value)
            }}/>

            <label>Number B:</label>
            <input value={b} name="Password" onChange={(event)=>{
                setB(event.target.value)
            }}/>

            <button onClick={()=>{
                let t1 = performance.now()
                calc(a,b)
                let t2 = performance.now()
                console.log(t2-t1)
            }}> Do the Memo Calc!</button>

        </form>
    );

}


function NotUsingMemo (props){

    const [a, setA] = useState(0)
    const [b, setB] = useState(0)


    function handleSubmit(event){
        event.preventDefault()

        let t1 = performance.now()
        calc(a,b)
        let t2 = performance.now()
        console.log("NO MEMO")
        console.log(t2-t1 + " Ms")

    }


    return(
        <form onSubmit={handleSubmit}>

            <label>Number A:</label>
            <input value={a} name="Email" onChange={(event)=>{
                setA(event.target.value)
            }}/>

            <label>Number B:</label>
            <input value={b} name="Password" onChange={(event)=>{
                setB(event.target.value)
            }}/>

            <button type="Submit"> Do the Calc!</button>

        </form>
    );

}

function calc(a, b){

    let loopA = 0
    let loopB = 0

    for(let i = 0; i <= a; i++){
        loopA += i
    }
    for(let i = 0; i <= b; i++){
        loopB += i
    }
    return(loopA+loopB);
}
