import { useRef, useState, useMemo } from "react"


export default function Memo(){

    return(
        <div>
            <UsingMemo/>
            <NotUsingMemo/>
        </div>

    );

}

function UsingMemo (props){

    const [a, setA] = useState(0)
    const [b, setB] = useState(0)

    let t1 = performance.now()
    const Bigmul = useMemo(()=>{
        let ans = 0;

        let loopA = 0
        let loopB = 0

        for(let i = 0; i <= a; i++){
            loopA += i
        }
        for(let i = 0; i <= b; i++){
            loopB += i
        }

        ans = loopA + loopB;
        return ans

    },[a,b]);

    let t2 = performance.now()

    function handleSubmit(event,a,b){
        event.preventDefault()

        let ans = Bigmul
        console.log("MEMO")
        console.log(ans)
        console.log(t2-t1 + " Ms")

    }


    return(
        <form onSubmit={handleSubmit}>

            <label>Number A:</label>
            <input value={a} name="Email" onChange={(event)=>{
                setA(event.target.value)
            }}/>

            <label>Number B:</label>
            <input value={b} name="Password" onChange={(event)=>{
                setB(event.target.value)
            }}/>

            <button type="Submit"> Do the Memo Calc!</button>

        </form>
    );

}


function NotUsingMemo (props){

    const [a, setA] = useState(0)
    const [b, setB] = useState(0)


    function handleSubmit(event){
        event.preventDefault()

        let t1 = performance.now()
        let loopA = 0
        let loopB = 0

        for(let i = 0; i <= a; i++){
            loopA += i
        }
        for(let i = 0; i <= b; i++){
            loopB += i
        }
        let t2 = performance.now()
        console.log("NO MEMO")
        console.log(loopA+loopA)
        console.log(t2-t1 + " Ms")

    }


    return(
        <form onSubmit={handleSubmit}>

            <label>Number A:</label>
            <input value={a} name="Email" onChange={(event)=>{
                setA(event.target.value)
            }}/>

            <label>Number B:</label>
            <input value={b} name="Password" onChange={(event)=>{
                setB(event.target.value)
            }}/>

            <button type="Submit"> Do the Calc!</button>

        </form>
    );

}

function calc(a, b){

    let loopA = 0
    let loopB = 0

    for(let i = 0; i <= a; i++){
        loopA += i
    }
    for(let i = 0; i <= b; i++){
        loopB += i
    }
    return(loopA+loopB);
}
