import { useCallback, useState } from 'react';

export default function UsingCallback(){

    const [a, setA] = useState(0)
    const [b, setB] = useState(0)



    const Addition = useCallback((a,b) => {
        console.log(a + b);
        return a+b

    }, []);

    return(
        <div>
            <button onClick={()=>{
                setA((value)=>{
                    return value + 1
                })
            }}> INC A</button>

            <button onClick={()=>{
                setB((value)=>{
                    return value + 1
                })
            }}> INC B</button>

            <button onClick={Addition}> Perform Addition</button>
        </div>

    );
}

