    const [b, setB] = useState(0)
    const [update, setUpdate] = useState(true)
    let counterRef = useRef(1)



    const ChildFunc = useCallback(() => {

        console.log("Here: " + update)

        counterRef.current += 1
        console.log(counterRef.current)


        return(
            <h1>RENDERED CHILD {counterRef.current}</h1>
        );


    }, [update]);


    return(
        <div>
            {ChildFunc()}

            <button onClick={()=>{
                setA((value)=>{
                    console.log("A: ")
                    console.log(value + 1)
                    return value + 1
                })
            }}> INC A</button>

            <button onClick={()=>{
                setB((value)=>{
                    console.log("B: ")
                    console.log( value + 1)
                    return value + 1
                })
            }}> INC B</button>

            <button onClick={()=>{
                setUpdate(!update)

            }}> Update </button>


        </div>

    );
}


