import { useRef, useState, useMemo } from "react"



export default function UsingMemo (props){

    const [a, setA] = useState(0)
    const [b, setB] = useState(0)

    const bigCalc = useMemo(()=>{
        console.log("INSIDE USE MEMO")
    },[a]);


    function handleSubmit(event){
        event.preventDefault()
    }

    return(
        <form onSubmit={handleSubmit}>

            <label>Number A:</label>
            <input value={a} name="Email" onChange={(event)=>{
                setA(event.target.value)
            }}/>

            <label>Number B:</label>
            <input value={b} name="Password" onChange={(event)=>{
                setB(event.target.value)
            }}/>

            <button onClick={()=>{
                 setB((value)=>{
                     return value + 1
                 })
            }}> SET B</button>

            <h1>{calc(a,b)}</h1>
            <button onClick={()=>{
                setA((value)=>{
                    return value + 1
                })
            }}>SET A</button>
        </form>
    );

}



function calc(a, b){

    let loopA = 0
    let loopB = 0

    for(let i = 0; i < a; i++){
        loopA += 1
    }
    for(let i = 0; i < b; i++){
        loopB += 1
    }
    return loopA+loopB;
}
