import { useRef, useState } from "react"

export default function UsingRef(props){

    const counterRef = useRef(10);
    let counterRefLess = 5;

    const [update, setUpdate] = useState(false);

    let beforeRenderingCheck = ""


    return(
        <div>
            <button onClick={()=>{
                console.log("useRef counter = " + ++counterRef.current);
            }}>Increment counter Reffed</button>

            <button onClick={()=>{
                console.log("normal counter = " + ++counterRefLess);
            }}>Increment counter Un-Reffed</button>

            <h1>{beforeRenderingCheck}</h1>
            <button onClick={()=>{
                setUpdate(!update);

                beforeRenderingCheck = "Before re-rendering: \n useRef counter =  {counterRef.current} normal counter = {counterRefLess}"



            }}>Re-render</button>
        </div>
    );




}
