import {useState} from 'react';


export default function ItemSwapper(props) {

    const [itemArray, setItermArray] = useState([
            {name:"Jonathan",isClicked:false},
            {name:"Joseph",isClicked:false},
            {name:"Dio",isClicked:false},
            {name:"Giorno",isClicked:false}
        ]
    )
    const [update, setUpdate] = useState(false)

    function listUsers(whichOnes){

        if(whichOnes === 'top'){
            const filteredArr = itemArray.filter(user => user.isClicked === false);
            const listItems = filteredArr.map((user) =>
                <li key={user.name} onClick={()=>{
                    user.isClicked=!user.isClicked
                    console.log(user.name,user.isClicked)

                }}>{user.name}</li>
            );
            return (
                <ul>{listItems} </ul>
            );
        }else{
            const filteredArr = itemArray.filter(user => user.isClicked === true);
            const listItems = filteredArr.map((user) =>
                <li key={user.name} onClick={()=>{
                    user.isClicked=!user.isClicked
                    console.log(user.name,user.isClicked)

                }}>{user.name}</li>
            );
            return (
                <ul>{listItems} </ul>
            );
        }




    }

    function HandleUpdate(){
        console.log(itemArray)
        setUpdate(!update)
    }


    return(
        <div>

            <h1>Top</h1>
            {listUsers("top")}
            <h1>------------</h1>
            <h1>Bottom</h1>
            {listUsers("bottom")}

            <button onClick={HandleUpdate}>UP</button>
            <button onClick={HandleUpdate}>DOWN</button>
        </div>


    );
}
