    const [email, setEmail] = useState('Empty');

    useEffect(()=>{
        console.log("EMPTY VALA")


    },[])

    useEffect(() => {
        console.log("USE EFFECT -> " +name)

    },[name]);

    useEffect(() => {
        console.log("USE EFFECT -> " +email)

    },[email]);



    let count = 0;

    return(
        <div>

      <h1>{name}</h1>
            <h1>{email}</h1>

        <button onClick={()=>{
            count++;
            setName("zeerak" + count)
        }}>Update Name</button>

            <button onClick={()=>{
                count++;
                setName("Email" + count)
            }}>Update Email</button>

        </div>
    );


}
